// Require necessary modules
var iterate    = require('./lib/iterate.js')
  , initState  = require('./lib/state.js')
  , terminator = require('./lib/terminator.js')
  ;

// Public API
module.exports = parallel;

/**
 * Runs iterator over provided array elements in parallel
 *
 * @param   {array|object} list - array or object (named list) to iterate over
 * @param   {function} iterator - iterator to run
 * @param   {function} callback - invoked when all elements processed
 * @returns {function} - jobs terminator
 */
function parallel(list, iterator, callback)
{
  // Initialize the state
  var state = initState(list);
// Iterate over the list in parallel until all elements are processed
  while (state.index < (state['keyedList'] || list).length)
  {
    // Call the iterate function to process the list elements
    iterate(list, iterator, state, function(error, result)
    {
      if (error)
      {
        callback(error, result);
        return;
      }

      // Check if it's the last element to be processed
      if (Object.keys(state.jobs).length === 0)
      {
        // If an error occurred during iteration, invoke the callback with the error
        callback(null, state.results);
        return;
      }
    });

    state.index++;
  }
// Return the terminator function bound to the state and callback
  return terminator.bind(state, callback);
}
